sys_script_include.script:
/* global Class, GlideRecordSecure, GlideStringUtil */
/* global global, gs */
/* eslint no-undef: "error" */
var BCSUtilClient = Class.create();
BCSUtilClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function () {
		global.AbstractAjaxProcessor.prototype.initialize.apply(this, arguments);
	},

	getScriptIncludeInfo: function () {
		var result = {
			status: 'error',
			message: ''
		};

		var scriptInclude = this.getParameter('script_include');
		if (GlideStringUtil.isEligibleSysID(scriptInclude)) {
			var scriptIncludeGr = new GlideRecordSecure('sys_script_include');
			scriptIncludeGr.addQuery('sys_id', scriptInclude);
			scriptIncludeGr.setLimit(1);
			scriptIncludeGr.query();
			if (scriptIncludeGr.next()) {
				result.script_include = {
					sys_id: scriptIncludeGr.getUniqueValue(),
					name: scriptIncludeGr.getDisplayValue(),
					scope: {
						sys_id: scriptIncludeGr.getValue('sys_scope'),
						display_value: scriptIncludeGr.getDisplayValue('sys_scope')
					}
				};

				result.status = 'success';
			} else {
				result.message = gs.getMessage('x_376096_bcs_record_unauthorized_or_invalid_record');
			}
		} else {
			result.message = gs.getMessage('x_376096_bcs_invalid_parameters');
		}

		return JSON.stringify(result);
	},

    type: 'BCSUtilClient'
});
